import { of } from 'rxjs';
import { catchError, mapTo } from 'rxjs/operators';
export class BaseApi {
  constructor(http) {
    this.http = http;
    this.baseUrl = 'http://localhost:3000/';
  }
  getUrl(url) {
    return this.baseUrl + url;
  }
  get(url = '') {
    return this.http.get(this.getUrl(url)).pipe(catchError(err => {
      console.log(err);
      return of(null);
    }));
  }
  post(url = '', data) {
    return this.http.post(this.getUrl(url), data).pipe(catchError(err => {
      console.log(err);
      return of(null);
    }));
  }
  put(url = '', data) {
    return this.http.put(this.getUrl(url), data).pipe(catchError(err => {
      console.log(err);
      return of(null);
    }));
  }
  delete(url, data) {
    return this.http.delete(this.getUrl(url + '/' + data['id'])).pipe(mapTo(data), catchError(err => {
      console.log(err);
      return of(null);
    }));
  }
}
